import React from 'react';
import { Heading } from '../Heading/Heading';
import './AboutMe.css';
import image from '../../images/about-me.png'

export const AboutMe = () => {
	return (
		<section className="about-me">
         <h2 className="main__title">Студент</h2>
			<div className="about-me__wrapper">
				<h3 className="about-me__title">Алина</h3>
				<p className="about-me__subtitle">Фронтенд-разработчик, 29 лет</p>
				<p className="about-me__text">Я родилась в Мурманске, живу в Москве с 2010 года, закончила факультет "Высшая школа перевода" МГУ им. М.В.Ломоносова. Я увлекаюсь музыкой и йогой. 
                В 2020 году во время пандемии начала изучать программирование. В октябре 2020 года начала проходить курс Веб-разработка на базе Яндекс.Практикума.</p>
				<div className="about-me__links">
				    <a className="about-me__link" href="https://github.com/Alina-Kova">Facebook</a>
					<a className="about-me__link" href="https://github.com/Alina-Kova">Github</a>
				</div>
				<img className="about-me__photo" src={image} alt="Моя фотография" />
			</div>
		</section>
	);
}
